package com.example.zyh.myexe;import java.util.ArrayList;public class People {	private long id;	private String name;	private String mobile;	private ArrayList<String> mobileList;		public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getMobile() {		return mobile;	}	public void setMobile(String mobile) {		this.mobile = mobile;	}	public ArrayList<String> getMobileList() {		return mobileList;	}	public void setMobileList(ArrayList<String> mobileList) {		this.mobileList = mobileList;	}}